package ge.market.flea.config;import ge.market.flea.telegram.FleaBot;import ge.market.flea.telegram.MessageReceiver;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Lazy;import org.springframework.scheduling.annotation.EnableScheduling;import org.telegram.abilitybots.api.sender.MessageSender;import org.telegram.abilitybots.api.sender.SilentSender;@Configuration@EnableSchedulingpublic class TelegramConfig {    @Autowired    private FleaBot fleaBot;    @Bean    public MessageReceiver messageReceiver() {       return new MessageReceiver(fleaBot.silent(), fleaBot.db());    }}