package ge.market.flea.telegram;import java.util.List;import java.util.Map;import org.telegram.abilitybots.api.sender.SilentSender;import org.telegram.telegrambots.meta.api.methods.send.SendMessage;import org.telegram.telegrambots.meta.api.objects.Update;public abstract class KeyboardAbstract {    private final SilentSender sender;    private final Map<Long, UserState> chatStates;    public KeyboardAbstract(SilentSender sender, Map<Long, UserState> chatStates) {        this.sender = sender;        this.chatStates = chatStates;    }    public abstract void messageResolver(Update update);    protected void messageSender(SendMessage message) {        sender.execute(message);    }    protected long getChatId (Update update) {        Long chatId;        if (update.getMessage() == null) {            chatId =  update.getCallbackQuery().getMessage().getChatId();        } else {            chatId = update.getMessage().getChat().getId();        }        return chatId;    }}