package ge.market.flea.telegram;import java.util.List;import org.telegram.telegrambots.meta.api.objects.replykeyboard.InlineKeyboardMarkup;import org.telegram.telegrambots.meta.api.objects.replykeyboard.ReplyKeyboard;import org.telegram.telegrambots.meta.api.objects.replykeyboard.ReplyKeyboardMarkup;import org.telegram.telegrambots.meta.api.objects.replykeyboard.buttons.InlineKeyboardButton;import org.telegram.telegrambots.meta.api.objects.replykeyboard.buttons.KeyboardRow;public class KeyboardFactory {    public static ReplyKeyboard getPizzaToppingsKeyboard() {        KeyboardRow row = new KeyboardRow();        row.add("Margherita");        row.add("Pepperoni");        return new ReplyKeyboardMarkup(List.of(row));    }    public static ReplyKeyboard getActionChoose() {        KeyboardRow row = new KeyboardRow();        row.add("Найти что-нибудь");        row.add("Сделать объявление");        row.add("Купить премиум");        row.add("Взорвать телефон");        return new ReplyKeyboardMarkup(List.of(row));    }    public static ReplyKeyboard getChooseLanguage(){        KeyboardRow row = new KeyboardRow();        row.add("ქართველი");        row.add("English");        row.add("Русский");    return new ReplyKeyboardMarkup(List.of(row));    }    public static ReplyKeyboard getYesOrNo() {        KeyboardRow row = new KeyboardRow();        row.add("Yes");        row.add("No");        return new ReplyKeyboardMarkup(List.of(row));    }}