package ge.market.flea.util;import java.util.ArrayList;import java.util.HashSet;import java.util.List;import org.bson.Document;import ge.market.flea.data.entity.Goods;public class Mapper {    public static Goods mapToGoods(Document document) {        Goods goods = new Goods();        goods.set_id(document.get("_id", Object.class));        goods.setGoodName(document.get("goodName", String.class));        goods.setDescription(document.get("description", String.class));        goods.setNameSplitted(document.get("nameSplitted", List.class));        goods.setTags(document.get("tags", List.class));        return goods;    }}